mapper.select:
SELECT A.* FROM (SELECT ROWNUM RR, RBOARD.* FROM (SELECT NUM, WRITER, SUBJECT, EMAIL, CONTENT, PASSWD, REG_DATE, READCNT, REF, RE_STEP, RE_LEVEL, ATTACHNM, FILENO, FILESIZE, ORGFILENM FROM BOARD ORDER BY REF DESC, RE_STEP ASC) RBOARD) A
WHERE NUM = #{num}
WHERE A.RR BETWEEN #{start} AND #{end}

mapper.update:
UPDATE BOARD 
   SET READCNT = READCNT+1
WHERE NUM = #{num}

UPDATE BOARD SET 
        SUBJECT= #{subject}
       , EMAIL= #{email}
       , CONTENT= #{content}
       , PASSWD= #{passwd}

       , ATTACHNM= #{attachNm}
       , FILENO=#{fileNo}
       , FILESIZE=#{fileSize}
       , ORGFILENM=#{orgFileNm}

        WHERE NUM = #{num}
